task playMusic()
{
    float timing_multiplier = 0.3;

    for (int i = 0; i < 269; i++)
    {
        if (notes[i][0] == -1)
        {
            sleep((int)(notes[i][1] * timing_multiplier));
        } else
        {
            playTone(notes[i][0], (int)(notes[i][1] * timing_multiplier));
            while(bSoundActive)
            {
                sleep(1);
            }
        }
    }
}

task edge_detection()
{
	while(1)
	{
		displayCenteredTextLine(6, "Colour value: %d", SensorValue[Colour]);
		displayCenteredTextLine(7, "Colour thresh: %d", white_colour/2);

		//if (SensorValue[Colour] < white_colour/2)
		if (SensorValue[Colour] < white_colour/2)
		{
			aquire_state_lock();
			current_state = EDGE_EVASION;
			interrupt = 1;
			release_state_lock();
		}
	}
}

task main()
{
	// Calibrate the colour sensor
	sleep(500);

	int colour_average = 0;
	for (int i = 0; i < 20; i++)
	{
		colour_average += SensorValue[Colour];
		//colour_average = getColorReflected(Colour);
		sleep(25);
	}
	white_colour = colour_average/20;

	// Zero the motor encoders
	resetMotorEncoder(FlipMotor);
	resetMotorEncoder(LeftMotor);
	resetMotorEncoder(RightMotor);

	startTask(drive_task);
	startTask(edge_detection);
	//startTask(playMusic);

	while(1)
	{
		//state_function[current_state]();
		// run the correct function for the current state
		switch (current_state)
		{
			case SEARCHING:
				searching();
				break;
			case MOVING:
				moving();
				break;
			case EDGE_EVASION:
				edge_evasion();
				break;
			case ATTACK:
				attack();
				break;
		}

		// Reset the interrupt flag
		interrupt = 0;
	}
}
